package 변수;

public class 기본데이터 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// 정수,실수, 문자1, 논리
		int age = 100;// 정수(4, 21억)
		double height = 122.2;// 실수(8)
		char gender = '남';// 문자 한글자==>''(싱글따옴표)
		boolean food = true; // 논리==> true(1)/false(0)

		String name = "홍길동";
		System.out.println("나의 아름은 " + name);
		System.out.println("나의 나이는 " + age);
		System.out.println("나의 키는 " + height);
		System.out.println("나의 성별은 " + gender);
		System.out.println("나의 식사여부 " + food);

	}

}
